* @Last Modified time: 2019-01-24 11:44:57
*/
const postReducer = (state={list:[{id:1,title:"你好"}]},action) => {
  switch(action.type){
    case 'LOAD_POSTS':
      return {
        ...state,list:action.payload
      }
    default:
      return state;
  }
}


export default postReducer
