* @Last Modified time: 2019-01-24 11:37:55
*/
import { getPosts } from '../serices/post_api';


export const loadPostsAction = async (dispatch) =>{
	const res = await getPosts()
	dispatch({
		type:'LOAD_POSTS',
		payload:res.data
	})
}
